struct CurvedTopShape: Shape {
    func path(in rect: CGRect) -> Path {
        var path = Path()

        let curveDepth: CGFloat = 30
        let curveWidth: CGFloat = 60

        // Start from top-left corner
        path.move(to: CGPoint(x: 0, y: curveDepth))

        // Top-left rounded corner
        path.addArc(
            center: CGPoint(x: curveDepth, y: curveDepth),
            radius: curveDepth,
            startAngle: .degrees(180),
            endAngle: .degrees(270),
            clockwise: false
        )

        // Top edge with inward curve in the middle
        let midX = rect.width / 2

        // Left side of top edge
        path.addLine(to: CGPoint(x: midX - curveWidth, y: 0))


        // Inward curve at top center
        path.addQuadCurve(
            to: CGPoint(x: midX + curveWidth, y: 0),
            control: CGPoint(x: midX, y: curveDepth)
        )

        // Right side of top edge
        path.addLine(to: CGPoint(x: rect.width - curveDepth, y: 0))

        // Top-right rounded corner
        path.addArc(
            center: CGPoint(x: rect.width - curveDepth, y: curveDepth),
            radius: curveDepth,
            startAngle: .degrees(270),
            endAngle: .degrees(0),
            clockwise: false
        )

        // Right edge
        path.addLine(to: CGPoint(x: rect.width, y: rect.height))

        // Bottom edge
        path.addLine(to: CGPoint(x: 0, y: rect.height))

        // Close path
        path.closeSubpath()

        return path
    }
}

