struct CryptoSwapView: View {
    @State private var fromToken: String = "ETH"
    @State private var toToken: String = "BTC"
    @State private var fromAmount: String = ""
    @State private var exchangeRate: Double = 0.0625

    var body: some View {
        VStack(spacing: 20) {
            // Header
            HStack {
                Text("Swap")
                    .font(.movilHeadline)
                    .foregroundColor(.movilText)
                Spacer()
                Button(action: {}) {
                    Image(systemName: "gear")
                        .foregroundColor(.movilTextSecondary)
                }
            }

            // From Token
            VStack(alignment: .leading, spacing: 8) {
                Text("From")
                    .font(.movilCaption)
                    .foregroundColor(.movilTextSecondary)

                HStack {
                    Button(action: {}) {
                        HStack {
                            Text(fromToken)
                                .font(.movilSubheadline)
                            Image(systemName: "chevron.down")
                                .font(.system(size: 10))
                        }
                        .foregroundColor(.movilText)
                        .padding(.horizontal, 12)
                        .padding(.vertical, 8)
                        .background(Color.movilCardBackground)
                        .clipShape(RoundedRectangle(cornerRadius: 12))
                        .overlay(
                            RoundedRectangle(cornerRadius: 12)
                                .stroke(Color.movilLightGray.opacity(0.3), lineWidth: 0.5)
                        )
                    }

                    Spacer()

                    TextField("0.0", text: $fromAmount)
                        .font(.movilHeadline)
                        .foregroundColor(.movilText)
                        .multilineTextAlignment(.trailing)
                        .textFieldStyle(.plain)
                }
            }
            .padding(16)
            .background(Color.movilCardBackground)
            .clipShape(RoundedRectangle(cornerRadius: 20))
            .overlay(
                RoundedRectangle(cornerRadius: 20)
                    .stroke(Color.movilLightGray.opacity(0.3), lineWidth: 0.5)
            )

            // Swap Arrow
            Button(action: {
                swapTokens()
            }) {
                Image(systemName: "arrow.up.arrow.down")
                    .font(.system(size: 16))
                    .foregroundColor(.movilOrange)
                    .frame(width: 40, height: 40)
                    .background(Color.movilCardBackground)
                    .clipShape(Circle())
                    .overlay(
                        Circle()
                            .stroke(Color.movilLightGray.opacity(0.3), lineWidth: 0.5)
                    )
            }

            // To Token
            VStack(alignment: .leading, spacing: 8) {
                Text("To")
                    .font(.movilCaption)
                    .foregroundColor(.movilTextSecondary)

                HStack {
                    Button(action: {}) {
                        HStack {
                            Text(toToken)
                                .font(.movilSubheadline)
                            Image(systemName: "chevron.down")
                                .font(.system(size: 10))
                        }
                        .foregroundColor(.movilText)
                        .padding(.horizontal, 12)
                        .padding(.vertical, 8)
                        .background(Color.movilCardBackground)
                        .clipShape(RoundedRectangle(cornerRadius: 12))
                        .overlay(
                            RoundedRectangle(cornerRadius: 12)
                                .stroke(Color.movilLightGray.opacity(0.3), lineWidth: 0.5)
                        )
                    }

                    Spacer()

                    Text(calculatedAmount)
                        .font(.movilHeadline)
                        .foregroundColor(.movilText)
                }
            }
            .padding(16)
            .background(Color.movilCardBackground)
            .clipShape(RoundedRectangle(cornerRadius: 20))
            .overlay(
                RoundedRectangle(cornerRadius: 20)
                    .stroke(Color.movilLightGray.opacity(0.3), lineWidth: 0.5)
            )

            // Exchange Rate
            HStack {
                Text("Rate: 1 \(fromToken) = \(String(format: \"%.6f\", exchangeRate)) \(toToken)")
                    .font(.movilCaption)
                    .foregroundColor(.movilTextSecondary)
                Spacer()
            }

            // Swap Button
            Button(action: {}) {
                Text("Swap")
                    .font(.movilButton)
                    .foregroundColor(.white)
                    .frame(maxWidth: .infinity)
                    .padding(.vertical, 14)
                    .background(Color.movilOrange)
                    .clipShape(RoundedRectangle(cornerRadius: 20))
                    .overlay(
                        RoundedRectangle(cornerRadius: 20)
                            .stroke(Color.movilOrange.opacity(0.3), lineWidth: 0.5)
                    )
            }
            .buttonStyle(PlainButtonStyle())
        }
        .padding(20)
    }

    private var calculatedAmount: String {
        guard let amount = Double(fromAmount) else { return "0.0" }
        return String(format: "%.6f", amount * exchangeRate)
    }

    private func swapTokens() {
        let temp = fromToken
        fromToken = toToken
        toToken = temp
    }
}
