import SwiftUI

struct EnergyFlowView: View {
    @State private var animationProgress: CGFloat = 0
    @State private var batteryLevel: Double = 0.65

    var body: some View {
        ZStack {
            Color.black
                .ignoresSafeArea()

            VStack(spacing: 40) {
                // Title
                Text("Energy Flow")
                    .font(.system(size: 32, weight: .bold))
                    .foregroundStyle(
                        LinearGradient(
                            colors: [.green, .cyan],
                            startPoint: .leading,
                            endPoint: .trailing
                        )
                    )

                // Battery with flowing lines
                ZStack {
                    // Background glow
                    Circle()
                        .fill(
                            RadialGradient(
                                colors: [.green.opacity(0.3), .clear],
                                center: .center,
                                startRadius: 50,
                                endRadius: 150
                            )
                        )
                        .frame(width: 300, height: 300)
                        .blur(radius: 30)

                    // Animated flowing lines
                    ForEach(0..<8) { index in
                        FlowingLine(
                            progress: animationProgress,
                            delay: Double(index) * 0.15
                        )
                        .stroke(
                            LinearGradient(
                                colors: [
                                    .clear,
                                    .green.opacity(0.8),
                                    .cyan.opacity(0.8),
                                    .clear
                                ],
                                startPoint: .leading,
                                endPoint: .trailing
                            ),
                            lineWidth: 2
                        )
                        .rotationEffect(.degrees(Double(index) * 45))
                    }

                    // Battery icon
                    BatteryView(level: batteryLevel)
                }
                .frame(height: 300)

                // Battery level
                VStack(spacing: 12) {
                    Text("\(Int(batteryLevel * 100))%")
                        .font(.system(size: 48, weight: .bold))
                        .foregroundColor(.green)

                    Text("Charging...")
                        .font(.subheadline)
                        .foregroundColor(.gray)
                }
            }
            .padding()
        }
        .onAppear {
            withAnimation(
                .linear(duration: 2)
                .repeatForever(autoreverses: false)
            ) {
                animationProgress = 1
            }
        }
    }
}

struct FlowingLine: Shape {
    var progress: CGFloat
    var delay: Double

    var animatableData: CGFloat {
        get { progress }
        set { progress = newValue }
    }

    func path(in rect: CGRect) -> Path {
        var path = Path()
        let adjustedProgress = max(0, min(1, progress - delay))

        if adjustedProgress > 0 {
            let startRadius: CGFloat = 60
            let endRadius: CGFloat = 120
            let radius = startRadius + (endRadius - startRadius) * adjustedProgress

            path.addArc(
                center: CGPoint(x: rect.midX, y: rect.midY),
                radius: radius,
                startAngle: .degrees(-90),
                endAngle: .degrees(-90 + 180 * Double(adjustedProgress)),
                clockwise: false
            )
        }
        return path
    }
}

struct BatteryView: View {
    let level: Double

    var body: some View {
        ZStack {
            // Battery outline
            RoundedRectangle(cornerRadius: 12)
                .stroke(Color.gray, lineWidth: 3)
                .frame(width: 80, height: 140)

            // Battery terminal
            RoundedRectangle(cornerRadius: 3)
                .fill(Color.gray)
                .frame(width: 40, height: 8)
                .offset(y: -74)

            // Battery fill
            VStack {
                Spacer()
                RoundedRectangle(cornerRadius: 8)
                    .fill(
                        LinearGradient(
                            colors: [.green, .cyan],
                            startPoint: .bottom,
                            endPoint: .top
                        )
                    )
                    .frame(height: 120 * level)
                    .padding(6)
            }
            .frame(width: 80, height: 140)
            .clipped()
        }
    }
}
