import SwiftUI

struct CustomSegmentedControl<T: Hashable>: View {
    let options: [T]
    let displayNames: [String]
    @Binding var selection: T

    @Environment(\.colorScheme) var colorScheme
    @Namespace private var animation

    var body: some View {
        HStack(spacing: 0) {
            ForEach(Array(options.enumerated()), id: \.offset) { index, option in
                Button(action: {
                    withAnimation(.spring(response: 0.3, dampingFraction: 0.7)) {
                        selection = option
                    }
                }) {
                    Text(displayNames[index])
                        .font(.system(size: 14, weight: .semibold))
                        .foregroundColor(
                            selection == option
                                ? (colorScheme == .dark ? .black : .white)
                                : (colorScheme == .dark ? .white.opacity(0.7) : .black.opacity(0.6))
                        )
                        .frame(maxWidth: .infinity)
                        .frame(height: 40)
                        .background(
                            ZStack {
                                if selection == option {
                                    RoundedRectangle(cornerRadius: 10)
                                        .fill(
                                            colorScheme == .dark
                                                ? Color.white
                                                : LinearGradient(
                                                    colors: [.blue, .blue.opacity(0.8)],
                                                    startPoint: .top,
                                                    endPoint: .bottom
                                                )
                                        )
                                        .matchedGeometryEffect(id: "segment", in: animation)
                                        .shadow(
                                            color: colorScheme == .dark
                                                ? .white.opacity(0.3)
                                                : .blue.opacity(0.4),
                                            radius: 8,
                                            x: 0,
                                            y: 4
                                        )
                                        .shadow(
                                            color: .black.opacity(0.1),
                                            radius: 2,
                                            x: 0,
                                            y: 2
                                        )
                                }
                            }
                        )
                        .contentShape(Rectangle())
                }
                .buttonStyle(PlainButtonStyle())
            }
        }
        .padding(4)
        .background(
            RoundedRectangle(cornerRadius: 12)
                .fill(
                    colorScheme == .dark
                        ? Color.black.opacity(0.6)
                        : Color.white
                )
                .shadow(
                    color: .black.opacity(colorScheme == .dark ? 0.5 : 0.1),
                    radius: 10,
                    x: 0,
                    y: 5
                )
        )
        .overlay(
            RoundedRectangle(cornerRadius: 12)
                .stroke(
                    colorScheme == .dark
                        ? Color.white.opacity(0.1)
                        : Color.black.opacity(0.05),
                    lineWidth: 1
                )
        )
    }
}

// Example usage
struct SegmentedControlExample: View {
    @State private var selectedTab = "Home"

    var body: some View {
        VStack(spacing: 40) {
            // String-based example
            CustomSegmentedControl(
                options: ["Home", "Search", "Profile"],
                displayNames: ["Home", "Search", "Profile"],
                selection: $selectedTab
            )
            .padding(.horizontal, 20)

            // Content based on selection
            Text("Selected: \(selectedTab)")
                .font(.headline)
                .foregroundColor(.secondary)

            Spacer()
        }
        .padding(.top, 50)
    }
}

