import SwiftUI

extension Color {
    static let foreColor = Color(#colorLiteral(red: 0.8614941239, green: 0.3432193995, blue: 0.9051209092, alpha: 1))
}

struct SplashView: View {

    @State private var presentTabsView = false
    @State private var animateGradient = false

    var body: some View {
        ZStack {
            LinearGradient(colors: [Color(.systemBackground), Color.blue, Color.black], startPoint: .top, endPoint: .bottom)
                .frame(maxWidth: .infinity, maxHeight: .infinity)
                .hueRotation(.degrees(animateGradient ? 90 : 0))
                .animation(.smooth, value: animateGradient)
                .onAppear {
                    withAnimation {
                        animateGradient.toggle()
                    }
                }
                .ignoresSafeArea(.all)
                .edgesIgnoringSafeArea(.all)

            VStack {
                Image(systemName: "circle.grid.cross.right.filled")
                    .font(.system(size: 26, weight: .light))
                    .foregroundStyle(Color.foreColor.opacity(0.8))
                    .background(
                        RoundedRectangle(cornerRadius: 15)
                            .fill(Color(.systemBackground).opacity(0.2))
                            .stroke(Color.white.opacity(0.3), lineWidth: 1)
                            .frame(width: 60, height: 60)
                    )
                    .padding(.top, 60)

                Spacer()

                HStack {
                    Text("Unleash Your \nCreativity in \nEvery Image".uppercased())
                        .font(.system(size: 36, weight: .light))
                        .multilineTextAlignment(.leading)
                        .fontWeight(.regular)
                        .lineSpacing(6)
                        .foregroundStyle(LinearGradient(colors: [Color(.systemBackground), Color(.systemBackground).opacity(0.6)], startPoint: .top, endPoint: .bottom))
                        .padding(.horizontal, 30)
                        .padding(.bottom, 35)
                    Spacer()
                }

                Button {
                    presentTabsView.toggle()
                } label: {
                    Text("Continue")
                        .font(.system(size: 18, weight: .medium))
                        .fontWeight(.semibold)
                        .foregroundStyle(Color.black)
                        .frame(height: 55)
                        .frame(maxWidth: .infinity)
                        .background(Color.white)
                        .cornerRadius(20)
                        .padding(.top, 40)
                        .padding(.horizontal, 30)
                        .padding(.bottom)

                }

                Text("By clicking Continue, you agree to our [Terms & Conditions](https://example.com) and [Privacy Policy](https://example.com).")
                    .lineSpacing(4)
                    .padding(.horizontal, 34)
                    .tint(Color.foreColor)
                    .accentColor(Color.foreColor)
                    .foregroundStyle(Color.white.opacity(0.8))
                    .font(.system(size: 14))
                    .multilineTextAlignment(.center)
                    .padding(.bottom, 20)
            }
        }
        .navigationBarHidden(true)
    }
}

