import SwiftUI

// MARK: - Free Trial Paywall
struct FreeTrialPaywallView: View {
    @Environment(\.dismiss) var dismiss
    @Environment(\.colorScheme) var colorScheme

    var body: some View {
        ZStack {
            // Background
            LinearGradient(
                gradient: Gradient(colors: backgroundColors),
                startPoint: .top,
                endPoint: .bottom
            )
            .ignoresSafeArea()

            VStack(spacing: 0) {
                // Top bar
                HStack {
                    Button {
                        dismiss()
                    } label: {
                        ZStack {
                            Circle()
                                .fill(closeButtonBackgroundColor)
                                .frame(width: 36, height: 36)

                            Image(systemName: "xmark")
                                .font(.system(size: 14, weight: .semibold))
                                .foregroundColor(closeButtonIconColor)
                        }
                    }

                    Spacer()

                    Button {
                        // Restore purchases
                    } label: {
                        Text("Restore")
                            .font(.system(size: 16, weight: .medium))
                            .foregroundColor(restoreButtonColor)
                    }
                }
                .padding(.horizontal, 20)

                Spacer()

                // Content
                VStack(spacing: 32) {
                    Text("Get your Free Trial\n today")
                        .font(.system(size: 34, weight: .bold))
                        .foregroundStyle(titleGradient)
                        .multilineTextAlignment(.center)
                        .lineSpacing(4)
                        .padding(.horizontal, 10)
                        .padding(.top, 20)

                    // Timeline
                    HStack(alignment: .top, spacing: 12) {
                        ZStack {
                            HStack {}
                                .frame(maxHeight: .infinity)
                                .frame(width: 50)
                                .background(
                                    LinearGradient(colors: [Color.clear, Color.yellow, Color.clear], startPoint: .top, endPoint: .bottom)
                                )
                                .padding(.leading)
                            Capsule()
                                .frame(width: 50)
                                .foregroundStyle(
                                    LinearGradient(colors: [Color.blue, Color(#colorLiteral(red: 0.9620351195, green: 0.4491090178, blue: 0.8503457904, alpha: 1)), Color.yellow], startPoint: .top, endPoint: .bottom)
                                )
                                .overlay(content: {
                                    RoundedRectangle(cornerRadius: 28)
                                        .stroke(Color.white.opacity(0.3), lineWidth: 2)
                                })
                                .padding(.leading)
                                .padding(.bottom, 60)
                            VStack(spacing: 86) {
                                Text("ðŸš€")

                                Text("ðŸ””")

                                Text("ðŸ’³")
                            }
                            .padding(.leading)
                            .padding(.bottom, 60)
                        }

                        VStack(spacing: 24) {
                            TimelineItem(
                                title: "Today",
                                description: "Access all the app's premium features now.",
                                colorScheme: colorScheme
                            )

                            TimelineItem(
                                title: "Day 5",
                                description: "Get notified when your free trail ends.",
                                colorScheme: colorScheme
                            )

                            TimelineItem(
                                title: "Day 7",
                                description: "You can cancel if you are not satisfied.",
                                isLast: true,
                                colorScheme: colorScheme
                            )
                        }
                        .padding(.horizontal, 20)
                    }

                    // Pricing
                    VStack(spacing: 12) {
                        Text("1 week free trial, then")
                            .font(.system(size: 18))
                            .foregroundColor(pricingSubtitleColor)

                        HStack(alignment: .firstTextBaseline, spacing: 4) {
                            Text("$19.99/year")
                                .font(.system(size: 24, weight: .bold))
                                .foregroundColor(pricingTitleColor)

                            Text("($1.62/mo)")
                                .font(.system(size: 18, weight: .medium))
                                .foregroundColor(pricingSubtitleColor)
                        }
                    }
                    .padding(.top, 10)

                    // CTA Button
                    VStack(spacing: 12) {
                        Button {
                            // Start trial
                        } label: {
                            Text("Start my 7-day free trial")
                                .font(.system(size: 18, weight: .semibold))
                                .foregroundColor(.white)
                                .frame(maxWidth: .infinity)
                                .frame(height: 56)
                                .background(
                                    LinearGradient(
                                        gradient: Gradient(colors: [
                                            Color.blue,
                                            Color.cyan
                                        ]),
                                        startPoint: .leading,
                                        endPoint: .trailing
                                    )
                                )
                                .overlay(content: {
                                    RoundedRectangle(cornerRadius: 28)
                                        .stroke(ctaButtonStrokeColor, lineWidth: 3)
                                })
                                .cornerRadius(28)
                        }

                        Text("Auto-renewable. Cancel anytime.")
                            .font(.system(size: 14))
                            .foregroundColor(footerTextColor)
                    }
                    .padding(.horizontal, 20)
                }
                .padding(.bottom, 40)
            }
        }
    }

    // MARK: - Computed Properties for Color Scheme Adaptation
    private var backgroundColors: [Color] {
        switch colorScheme {
        case .dark:
            return [
                Color(red: 0.1, green: 0.1, blue: 0.2),
                Color.black
            ]
        case .light:
            return [
                Color(red: 0.75, green: 0.75, blue: 0.98),
                Color.white
            ]
        @unknown default:
            return [
                Color(red: 0.1, green: 0.1, blue: 0.2),
                Color.black
            ]
        }
    }

    private var closeButtonBackgroundColor: Color {
        switch colorScheme {
        case .dark:
            return Color.white.opacity(0.1)
        case .light:
            return Color.black.opacity(0.1)
        @unknown default:
            return Color.white.opacity(0.1)
        }
    }

    private var closeButtonIconColor: Color {
        switch colorScheme {
        case .dark:
            return .white
        case .light:
            return .black
        @unknown default:
            return .white
        }
    }

    private var restoreButtonColor: Color {
        switch colorScheme {
        case .dark:
            return .white.opacity(0.6)
        case .light:
            return .black.opacity(0.6)
        @unknown default:
            return .white.opacity(0.6)
        }
    }

    private var titleGradient: LinearGradient {
        switch colorScheme {
        case .dark:
            return LinearGradient(
                colors: [.white.opacity(0.9), .white.opacity(0.4)],
                startPoint: .topLeading,
                endPoint: .bottomTrailing
            )
        case .light:
            return LinearGradient(
                colors: [.black.opacity(0.9), .black.opacity(0.6)],
                startPoint: .topLeading,
                endPoint: .bottomTrailing
            )
        @unknown default:
            return LinearGradient(
                colors: [.white.opacity(0.9), .white.opacity(0.4)],
                startPoint: .topLeading,
                endPoint: .bottomTrailing
            )
        }
    }

    private var pricingSubtitleColor: Color {
        switch colorScheme {
        case .dark:
            return .white.opacity(0.7)
        case .light:
            return .black.opacity(0.7)
        @unknown default:
            return .white.opacity(0.7)
        }
    }

    private var pricingTitleColor: Color {
        switch colorScheme {
        case .dark:
            return .white
        case .light:
            return .black
        @unknown default:
            return .white
        }
    }

    private var ctaButtonStrokeColor: Color {
        switch colorScheme {
        case .dark:
            return Color.white.opacity(0.4)
        case .light:
            return Color.white.opacity(0.3)
        @unknown default:
            return Color.white.opacity(0.4)
        }
    }

    private var footerTextColor: Color {
        switch colorScheme {
        case .dark:
            return .white.opacity(0.7)
        case .light:
            return .black.opacity(0.7)
        @unknown default:
            return .white.opacity(0.7)
        }
    }
}

// MARK: - Timeline Item
struct TimelineItem: View {
    let title: String
    let description: String
    var isLast: Bool = false
    var colorScheme: ColorScheme

    var body: some View {
        HStack(alignment: .top, spacing: 20) {
            VStack(alignment: .leading, spacing: 6) {
                Text(title)
                    .font(.system(size: 22, weight: .bold))
                    .foregroundColor(titleColor)

                Text(description)
                    .font(.system(size: 17))
                    .foregroundColor(descriptionColor)
                    .lineSpacing(3)
            }
            .padding(.top, 10)

            Spacer()
        }
    }

    private var titleColor: Color {
        switch colorScheme {
        case .dark:
            return .white
        case .light:
            return .black
        @unknown default:
            return .white
        }
    }

    private var descriptionColor: Color {
        switch colorScheme {
        case .dark:
            return .white.opacity(0.6)
        case .light:
            return .black.opacity(0.6)
        @unknown default:
            return .white.opacity(0.6)
        }
    }
}

