struct PaywallPreviews: PreviewProvider {
    static var previews: some View {
        Group {
            FreeTrialPaywallView()
                .previewDisplayName("Free Trial - Dark")
                .preferredColorScheme(.dark)

            FreeTrialPaywallView()
                .previewDisplayName("Free Trial - Light")
                .preferredColorScheme(.light)
        }
    }
}
