import SwiftUI

extension Color {
    static let tint = Color(red: 0.38, green: 0.28, blue: 0.48)
}

struct DayItem {
    let letter: String
    let number: String
    let isSelected: Bool
}

struct TaskManagementView: View {
    let headerTitle: String
    let headerDate: String
    let progressValue: Double
    let progressColor: Color

    let focusLabel: String
    let focusIcon: String
    let taskTitle: String
    let taskDescription: String
    let buttonText: String
    let timeEstimate: String

    let nextUpLabel: String
    let nextUpTask: String

    let weekLabel: String
    let weekDateRange: String
    let days: [DayItem]

    let inputPlaceholder: String
    let onInputAction: () -> Void

    var body: some View {
        ZStack {
            LinearGradient(
                colors: [
                    Color(.systemBackground),
                    Color(red: 0.62, green: 0.20, blue: 0.38).opacity(0.3),
                    Color(red: 0.48, green: 0.28, blue: 0.48).opacity(0.4),
                    Color(red: 0.75, green: 0.82, blue: 0.92),
                    Color(.systemBackground),
                    Color(.systemBackground)
                ],
                startPoint: .topLeading,
                endPoint: .bottom
            )
            .ignoresSafeArea()

            VStack {}
                .frame(width: UIScreen.main.bounds.width, height: UIScreen.main.bounds.height)
                .background(.regularMaterial)
                .ignoresSafeArea()
                .opacity(0.5)

            ScrollView {
                VStack(alignment: .leading, spacing: 24) {
                    headerSection

                    bottomInputField

                    ZStack {
                        nextUpSection
                        mainTaskCard
                            .padding(.bottom, 89)
                    }

                    weeklyCalendarSection
                }
                .padding(.horizontal, 24)
                .padding(.top, 80)
            }
        }
    }

    private var headerSection: some View {
        HStack {
            VStack(alignment: .leading, spacing: 4) {
                Text(headerTitle)
                    .font(.system(size: 36, weight: .medium))
                    .foregroundColor(.primary)
                    .kerning(-1)

                Text(headerDate)
                    .font(.system(size: 34, weight: .regular))
                    .foregroundColor(Color.white.opacity(0.6))
                    .kerning(-1)
            }

            Spacer()

            CircularProgressView(
                progress: progressValue,
                color: progressColor
            )
        }
    }

    private var mainTaskCard: some View {
        VStack(alignment: .leading, spacing: 16) {
            HStack {
                Text(focusLabel)
                    .font(.system(size: 14, weight: .medium))
                    .foregroundColor(.gray)

                Spacer()

                Button(action: {}) {
                    ZStack {
                        Circle()
                            .fill(Color.white)
                            .frame(width: 40, height: 40)

                        Image(systemName: focusIcon)
                            .font(.system(size: 14, weight: .bold))
                            .foregroundColor(Color.tint)
                            .offset(x: 1)
                    }
                }
            }

            Text(taskTitle)
                .font(.system(size: 26, weight: .regular))
                .foregroundColor(.primary)
                .lineSpacing(1)
                .kerning(-1)

            Text(taskDescription)
                .font(.system(size: 14, weight: .regular))
                .foregroundColor(.primary.opacity(0.7))
                .lineSpacing(2)

            Spacer()

            Button(action: {}) {
                HStack(spacing: 8) {
                    Text(buttonText)
                        .font(.system(size: 17, weight: .medium))
                        .foregroundColor(Color(.systemBackground))
                        .padding(.leading)
                        .kerning(-1)

                    Text(timeEstimate)
                        .font(.system(size: 15, weight: .regular))
                        .foregroundColor(Color(.systemBackground).opacity(0.75))
                }
                .frame(maxWidth: .infinity)
                .padding(.vertical, 16)
                .background(
                    RoundedRectangle(cornerRadius: 28)
                        .fill(LinearGradient(colors: [Color.primary, Color.primary.opacity(0.8)], startPoint: .leading, endPoint: .trailing))
                )
                .glassEffect(.regular, in: .rect(cornerRadius: 28))
            }
        }
        .padding(24)
        .frame(height: 400)
        .background(
            RoundedRectangle(cornerRadius: 38)
                .fill(LinearGradient(colors: [Color.gray.opacity(0.2), Color(.systemBackground).opacity(0.98)], startPoint: .top, endPoint: .bottom))
        )
        .glassEffect(.regular, in: .rect(cornerRadius: 38))
        .shadow(color: .black.opacity(0.08), radius: 24, x: 0, y: 8)
    }

    private var nextUpSection: some View {
        VStack(alignment: .leading, spacing: 8) {
            Spacer()

            Text(nextUpLabel)
                .font(.system(size: 14, weight: .medium))
                .foregroundColor(.gray)

            HStack {
                Text(nextUpTask)
                    .font(.system(size: 18, weight: .medium))
                    .kerning(-0.5)
                    .foregroundColor(.primary)
                    .multilineTextAlignment(.center)
                    .frame(alignment: .leading)

                Spacer()
            }
            .frame(maxWidth: .infinity)
        }
        .padding(24)
        .frame(height: 490)
        .background(
            RoundedRectangle(cornerRadius: 38)
                .fill(LinearGradient(colors: [Color.gray.opacity(0.0), Color(.systemBackground).opacity(0.28)], startPoint: .top, endPoint: .bottom))
        )
        .glassEffect(.regular, in: .rect(cornerRadius: 38))
        .shadow(color: .black.opacity(0.08), radius: 24, x: 0, y: 8)
    }

    private var weeklyCalendarSection: some View {
        VStack(alignment: .leading, spacing: 12) {
            HStack {
                Text(weekLabel)
                    .font(.system(size: 14, weight: .medium))
                    .foregroundColor(.gray)

                Spacer()

                Text(weekDateRange)
                    .font(.system(size: 14, weight: .medium))
                    .foregroundColor(.primary)
            }
            .padding(.horizontal, 26)

            ScrollView(.horizontal, showsIndicators: false) {
                HStack(spacing: 8) {
                    Spacer()
                    ForEach(Array(days.enumerated()), id: \.offset) { index, day in
                        DayCapsule(
                            letter: day.letter,
                            number: day.number,
                            isSelected: day.isSelected
                        )
                    }
                    Spacer()
                }
            }
        }
    }

    private var bottomInputField: some View {
        Button(action: onInputAction) {
            HStack {
                Image(systemName: "sparkles.2")
                    .foregroundStyle(
                        LinearGradient(
                            colors: [
                                Color(red: 0.62, green: 0.20, blue: 0.38),

                                Color(red: 0.48, green: 0.28, blue: 0.48),

                                Color(red: 0.75, green: 0.82, blue: 0.92)
                            ],
                            startPoint: .top,
                            endPoint: .bottomTrailing
                        )
                    )

                Text(inputPlaceholder)
                    .font(.system(size: 17, weight: .regular))
                    .foregroundColor(Color(red: 0.6, green: 0.6, blue: 0.6))

                Spacer()
            }
            .padding(.horizontal, 20)
            .padding(.vertical, 16)
            .background(
                RoundedRectangle(cornerRadius: 28)
                    .fill(LinearGradient(colors: [Color(red: 0.92, green: 0.92, blue: 0.92).opacity(0.2), Color.white.opacity(0.1)], startPoint: .leading, endPoint: .trailing))
            )
            .glassEffect(.regular, in: .rect(cornerRadius: 28))
        }
    }
}

struct CircularProgressView: View {
    let progress: Double
    let color: Color

    var body: some View {
        VStack(spacing: 6) {
            ZStack {
                Circle()
                    .stroke(Color.white.opacity(0.45), lineWidth: 1.3)
                    .frame(width: 86, height: 86)

                Circle()
                    .trim(from: 0, to: progress)
                    .stroke(color, style: StrokeStyle(lineWidth: 2, lineCap: .round))
                    .frame(width: 86, height: 86)
                    .rotationEffect(.degrees(-90))

                VStack(alignment: .center, spacing: 2) {
                    Text("\(Int(progress * 100))%")
                        .font(.system(size: 17, weight: .medium))
                        .foregroundColor(.primary.opacity(0.7))

                    Text("Overall\nprogress")
                        .font(.system(size: 10, weight: .medium))
                        .foregroundColor(Color.primary.opacity(0.4))
                        .multilineTextAlignment(.center)
                }
            }
        }
    }
}

struct DayCapsule: View {
    let letter: String
    let number: String
    let isSelected: Bool

    var body: some View {
        VStack(spacing: 8) {
            Text(letter)
                .font(.system(size: 13, weight: .regular))
                .foregroundColor(isSelected ? .primary : Color(red: 0.5, green: 0.5, blue: 0.5))

            Text(number)
                .font(.system(size: 17, weight: .regular))
                .foregroundColor(isSelected ? .black : Color(red: 0.5, green: 0.5, blue: 0.5))
                .padding(.bottom, 4)

            Circle()
                .fill(isSelected ? Color.black : Color(red: 0.7, green: 0.7, blue: 0.7))
                .frame(width: 4, height: 4)
        }
        .frame(width: 48, height: 82)
        .background(
            RoundedRectangle(cornerRadius: 20)
                .fill(isSelected ? Color.white : Color.clear)
        )
        .glassEffect(isSelected ? .clear : .identity, in: .rect(cornerRadius: 20))
        .padding(.vertical, 16)
        .padding(.top, -6)
    }
}

extension View {
    @ViewBuilder
    func glassEffect() -> some View {
        if #available(iOS 18.0, *) {
            self.background(.ultraThinMaterial)
                .overlay(
                    RoundedRectangle(cornerRadius: 0)
                        .stroke(
                            LinearGradient(
                                colors: [
                                    Color(.systemBackground).opacity(0.6),
                                    Color(.systemBackground).opacity(0.1)
                                ],
                                startPoint: .topLeading,
                                endPoint: .bottomTrailing
                            ),
                            lineWidth: 1
                        )
                )
        } else {
            self.background(
                ZStack {
                    Color(.systemBackground).opacity(0.95)
                    MaterialView(style: .systemMaterial)
                }
            )
            .overlay(
                RoundedRectangle(cornerRadius: 0)
                    .stroke(
                        LinearGradient(
                            colors: [
                                Color(.systemBackground).opacity(0.6),
                                Color(.systemBackground).opacity(0.1)
                            ],
                            startPoint: .topLeading,
                            endPoint: .bottomTrailing
                        ),
                        lineWidth: 1
                    )
            )
        }
    }
}

struct MaterialView: UIViewRepresentable {
    let style: UIBlurEffect.Style

    func makeUIView(context: Context) -> UIVisualEffectView {
        UIVisualEffectView(effect: UIBlurEffect(style: style))
    }

    func updateUIView(_ uiView: UIVisualEffectView, context: Context) {
        uiView.effect = UIBlurEffect(style: style)
    }
}

