import SwiftUI

struct Transaction: Identifiable {
    let id = UUID()
    let name: String
    let date: Date
    let amount: Double
    let iconURL: String
    let type: TransactionType

    enum TransactionType {
        case expense
        case income
    }

    var formattedDate: String {
        let formatter = DateFormatter()
        formatter.dateFormat = "MMM dd, yyyy • hh:mm a"
        return formatter.string(from: date)
    }

    var formattedAmount: String {
        let sign = type == .income ? "+" : "-"
        return "\(sign)$\(String(format: "%.2f", abs(amount)))"
    }
}

struct AccountsView: View {
    @Environment(\.colorScheme) var colorScheme
    @State private var balance: Double = 12432.33
    @State private var selectedCurrency: String = "USD"
    @State private var accountType: String = "General"

    let transactions: [Transaction] = [
        Transaction(
            name: "Walmart",
            date: Date(),
            amount: -13.93,
            iconURL: "https://www.allrecipes.com/thmb/V0uJ5-OXuEY2ozGrx16E-QOw53I=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/ar-new-walmart-logo-spark-blue-4x3-05716be5a80a49858b751defdd5c3705.jpg",
            type: .expense
        ),
        Transaction(
            name: "Twitch",
            date: Date(),
            amount: -11.99,
            iconURL: "https://cdn-icons-png.flaticon.com/512/3991/3991943.png",
            type: .expense
        ),
        Transaction(
            name: "Dorian Hale",
            date: Date(),
            amount: 325.00,
            iconURL: "https://example.com/person_icon.png",
            type: .income
        )
    ]

    var body: some View {
        TabView {
            NavigationStack {
                ZStack {
                    RadialGradient(
                        gradient: Gradient(colors: [
                            Color.cyan.opacity(colorScheme == .dark ? 0.2 : 0.3),
                            Color(colorScheme == .dark ? .systemBackground : .white),
                            Color.cyan.opacity(colorScheme == .dark ? 0.05 : 0.1)
                        ]),
                        center: .topTrailing,
                        startRadius: 100,
                        endRadius: 600
                    )
                    .ignoresSafeArea()

                    VStack(spacing: 0) {
                        ScrollView(showsIndicators: false) {
                            VStack(alignment: .leading, spacing: 24) {
                                Text("Accounts")
                                    .font(.system(size: 28, weight: .medium))
                                    .foregroundColor(.primary)
                                    .padding(.horizontal, 20)
                                    .padding(.top, -20)

                                balanceCard
                                    .padding(.horizontal, 20)
                                    .padding(.top, 40)

                                actionButtons
                                    .padding(.horizontal, 20)
                                    .padding(.top, 24)

                                Spacer()

                                transactionsSection
                                    .padding(.top, 50)
                                    .ignoresSafeArea(.all)
                            }
                        }
