                    ToolbarItem(placement: .topBarTrailing) {
                        HStack(spacing: 16) {
                            Button {} label: {
                                Image(systemName: "magnifyingglass")
                                    .font(.system(size: 16))
                                    .foregroundColor(.secondary)
                            }

                            Button {} label: {
                                Image(systemName: "square.grid.2x2")
                                    .font(.system(size: 16))
                                    .foregroundColor(.secondary)
                            }
                        }
                    }
                }
            }
            .tabItem {
                Image(systemName: "person.fill")
            }

            HStack {}
                .tabItem {
                    Image(systemName: "chart.bar")
                }
        }
        .tint(.primary)
    }

    private var pageIndicator: some View {
        HStack(spacing: 6) {
            Circle().fill(Color.primary).frame(width: 6, height: 6)
            Circle().fill(Color.secondary.opacity(0.3)).frame(width: 6, height: 6)
            Circle().fill(Color.secondary.opacity(0.3)).frame(width: 6, height: 6)
        }
        .frame(maxWidth: .infinity)
    }

    private var balanceCard: some View {
        VStack(alignment: .leading, spacing: 8) {
            HStack(spacing: 8) {
                Text(accountType).font(.system(size: 14)).foregroundColor(.secondary)
                Text("•").foregroundColor(.secondary)
                Text(selectedCurrency).font(.system(size: 14)).foregroundColor(.secondary)
            }

            HStack(alignment: .firstTextBaseline, spacing: 0) {
                Text("$").font(.system(size: 32)).foregroundColor(.primary)
                Text(String(format: "%.0f", floor(balance)))
                    .font(.system(size: 56, weight: .light))
                    .foregroundColor(.primary)
                Text(String(format: ".%02d", Int((balance.truncatingRemainder(dividingBy: 1)) * 100)))
                    .font(.system(size: 32))
                    .foregroundColor(.secondary.opacity(0.5))
            }
        }
    }

    private var actionButtons: some View {
        HStack(spacing: 24) {
            Button {} label: { Image(systemName: "plus").font(.system(size: 20)).foregroundColor(.secondary) }
            Button {} label: { Image(systemName: "arrow.down").font(.system(size: 20)).foregroundColor(.secondary) }
            Button {} label: { Image(systemName: "archivebox").font(.system(size: 20)).foregroundColor(.secondary) }
            Spacer()
            Button {} label: { Image(systemName: "ellipsis").font(.system(size: 20)).foregroundColor(.secondary) }
        }
    }

    private var transactionsSection: some View {
        VStack(alignment: .leading, spacing: 16) {
            HStack {
                Text("Transactions")
                    .font(.system(size: 22, weight: .medium))
                Spacer()
                Button {} label: {
                    Text("All")
                        .font(.system(size: 16, weight: .medium))
                        .foregroundColor(.secondary)
                        .underline()
                }
            }
            .padding(.horizontal, 20)

            VStack(spacing: 0) {
                ForEach(transactions) { transaction in
                    TransactionRow(transaction: transaction)
                }
            }

            Spacer()
        }
        .padding(.horizontal, 4)
        .padding(.vertical, 28)
        .background(Color(.systemBackground))
        .cornerRadius(40)
        .shadow(color: colorScheme == .dark ? Color.clear : Color.gray.opacity(0.2), radius: 20)
        .ignoresSafeArea(edges: .bottom)
    }
}

struct TransactionRow: View {
    @Environment(\.colorScheme) var colorScheme
    let transaction: Transaction

    var body: some View {
        HStack(spacing: 16) {
            AsyncImage(url: transaction.iconURL.isEmpty ? nil : URL(string: transaction.iconURL)) { image in
                image.resizable().scaledToFill()
            } placeholder: {
                Image(systemName: "person").foregroundColor(.secondary.opacity(0.3))
            }
            .frame(width: 44, height: 44)
            .background(Color.secondary.opacity(0.1))
            .cornerRadius(22)

            VStack(alignment: .leading, spacing: 4) {
                Text(transaction.name).font(.system(size: 16, weight: .medium))
                Text(transaction.formattedDate).font(.system(size: 13)).foregroundColor(.secondary)
            }

            Spacer()

            Text(transaction.formattedAmount)
                .font(.system(size: 16, weight: .light))
                .foregroundColor(transaction.type == .income ? .green : .primary)
        }
        .padding(.horizontal, 20)
        .padding(.vertical, 12)
    }
}

